import {
  Heading,
  Stack,
  Box,
  Divider,
  Text,
  SimpleGrid,
} from '@chakra-ui/core';
import Layout from '../../components/layout/Layout';

const Content = ({ head, text, children }) => {
  return (
    <Stack spacing={8}>
      <Box p={5} shadow='md' borderWidth='1px'>
        <Heading fontSize='xl'>{head}</Heading>
        <Text mt={4}>{text}</Text>

        {children}
      </Box>
    </Stack>
  );
};
export default (props) => {
  return (
    <Layout>
      <Heading as='h2' size='lg'>
        Nombre del Curso
      </Heading>
      <Divider></Divider>

      <SimpleGrid columns={4} spacing={2}>
        <Box>
          <Content head='Duracion' text={'20 Hrs'}></Content>
        </Box>
        <Box>
          <Content head='Precio' text='$300.00MXN'></Content>
        </Box>
        <Box>
          <Content head='Categoria' text='Tecnologia'></Content>
        </Box>
        <Box>
          <Content head='Categoria' text='Tecnologia'></Content>
        </Box>
      </SimpleGrid>

      <SimpleGrid columns={2} spacing={2}>
        <Box>
          <Content
            head='Descripcion'
            text={'Aqui va la descrpcion del curso'}
          ></Content>
        </Box>
        <Box>
          {' '}
          <Content head='Temario'></Content>
        </Box>
      </SimpleGrid>
      <Content></Content>
    </Layout>
  );
};
