import Layout from '../components/layout/Layout';
import moment from 'moment';
import {
  SimpleGrid,
  Box,
  Heading,
  Tabs,
  TabList,
  TabPanels,
  Tab,
  TabPanel,
  Flex,
  Input,
  Button,
} from '@chakra-ui/core';
import { useState, useRef, useEffect } from 'react';
import Head from 'next/head';

const Msg = (props) => {
  const myRef = useRef(null);
  useEffect(() => {
    //myRef.current.scrollIntoView();
  }, []);
  return (
    <Flex
      ref={myRef}
      flexDirection='column'
      justifyContent='center'
      alignItems='flex-end'
      w='95%'
    >
      <Flex>
        <span style={{ color: '#a8aab1', marginRight: '20px' }}>
          {moment().format('DD MM YYYY hh mm A')}
        </span>
        <span>Joseph</span>
      </Flex>
      <div classNameName='message wow-animate'>{props.children}</div>
    </Flex>
  );
};
export const Live = (props) => {
  const [msgs, setMsgs] = useState([]);
  const [notes, setNotes] = useState([]);
  const [textNote, setTextNote] = useState('');
  const [text, setText] = useState('');

  const sendMsg = () => {
    let newmsg = [...msgs];

    if (text != '') {
      newmsg.push(text);
      setMsgs(newmsg);
      setText('');
    }
  };
  const SendNote = () => {
    let newNotes = [...notes];
    if (textNote != '') {
      newNotes.push(textNote);
      setNotes(newNotes);
      setTextNote('');
    }
  };
  useEffect(() => {
    OpenPay.setId('mqrfjs0h2c8pxopp6c63');
    OpenPay.setApiKey('pk_0da9497fbffc4c5996b42ce351972d92');
    var deviceSessionId = OpenPay.deviceData.setup(
      'payment-form',
      'deviceIdHiddenFieldName'
    );
  }, []);
  return (
    <Layout>
      <Head>
        <head>
          <script
            type='text/javascript'
            src='https://resources.openpay.mx/lib/openpay-js/1.2.38/openpay.v1.min.js'
          ></script>
        </head>
      </Head>
      <SimpleGrid columns={[1, 0, 2]}>
        <Box>
          <form action='#' method='POST' id='payment-form'>
            <input type='hidden' name='token_id' id='token_id' />
            <input
              type='hidden'
              name='use_card_points'
              id='use_card_points'
              value='false'
            />
            <div className='pymnt-itm card active'>
              <h2>Tarjeta de crédito o débito</h2>
              <div className='pymnt-cntnt'>
                <div className='card-expl'>
                  <div className='credit'>
                    <h4>Tarjetas de crédito</h4>
                  </div>
                  <div className='debit'>
                    <h4>Tarjetas de débito</h4>
                  </div>
                </div>
                <div className='sctn-row'>
                  <div className='sctn-col l'>
                    <label>Nombre del titular</label>
                    <input
                      type='text'
                      placeholder='Como aparece en la tarjeta'
                      autoComplete='off'
                      data-openpay-card='holder_name'
                    />
                  </div>
                  <div className='sctn-col'>
                    <label>Número de tarjeta</label>
                    <input
                      type='text'
                      autoComplete='off'
                      data-openpay-card='card_number'
                    />
                  </div>
                </div>
                <div className='sctn-row'>
                  <div className='sctn-col l'>
                    <label>Fecha de expiración</label>
                    <div className='sctn-col half l'>
                      <input
                        type='text'
                        placeholder='Mes'
                        data-openpay-card='expiration_month'
                      />
                    </div>
                    <div className='sctn-col half l'>
                      <input
                        type='text'
                        placeholder='Año'
                        data-openpay-card='expiration_year'
                      />
                    </div>
                  </div>
                  <div className='sctn-col cvv'>
                    <label>Código de seguridad</label>
                    <div className='sctn-col half l'>
                      <input
                        type='text'
                        placeholder='3 dígitos'
                        autoComplete='off'
                        data-openpay-card='cvv2'
                      />
                    </div>
                  </div>
                </div>
                <div className='openpay'>
                  <div className='logo'>Transacciones realizadas vía:</div>
                  <div className='shield'>
                    Tus pagos se realizan de forma segura con encriptación de
                    256 bits
                  </div>
                </div>
                <div className='sctn-row'>
                  <a className='button rght' id='pay-button'>
                    Pagar
                  </a>
                </div>
              </div>
            </div>
          </form>
        </Box>
      </SimpleGrid>
    </Layout>
  );
};
export default Live;
