import { useState } from 'react';
import { Box, Flex, Image, Heading } from '@chakra-ui/core';
import { RiMenuLine } from 'react-icons/ri';
const Navbar = (props) => {
  const [toggle, setToggle] = useState(false);
  return (
    <div className='navBar'>
      <Flex w='100%' justifyContent='space-between' alignItems='center'>
        <Flex alignItems='center'>
          <Flex alignItems='center' cursor='pointer'>
            <Image src='/img/logo.png' h='50px' />
            <Heading ml='10px'>Crewdemy</Heading>
          </Flex>
          <Flex h='100%' alignItems='center' className='opts-large' ml='20px'>
            <Box cursor='pointer' ml='10px'>
              Cursos presenciales
            </Box>
            <Box cursor='pointer' ml='10px'>
              Nosotros
            </Box>
            <Box cursor='pointer' ml='10px'>
              Crewsoft
            </Box>
          </Flex>
        </Flex>

        <button
          onClick={() => {
            setToggle(!toggle);
          }}
        >
          <Box as={RiMenuLine} />
        </button>
      </Flex>
      <ul className={toggle ? 'links show-nav' : 'no-links'}>
        <li href='#'>Home</li>
        <li href='#'>About us</li>
        <li href='#'>Contact</li>
      </ul>
    </div>
  );
};

export default Navbar;
