import { Heading, Stack, Box, Divider, Text } from '@chakra-ui/core';
import Layout from '../../components/layout/Layout';

export default (props) => {
  return (
    <Layout>
      <Heading as='h2' size='lg'>
        Nombre del Curso
      </Heading>
      <Divider></Divider>

      <Stack spacing={8}>
        <Box p={5} shadow='md' borderWidth='1px'>
          <Heading fontSize='xl'>Descripcion del curso</Heading>
          <Text mt={4}>Duracion 20 HRS</Text>
        </Box>
      </Stack>
    </Layout>
  );
};
