import Layout from '../components/layout/Layout';
import moment from 'moment';
import {
  SimpleGrid,
  Box,
  Heading,
  Tabs,
  TabList,
  TabPanels,
  Tab,
  TabPanel,
  Flex,
  Input,
  Button,
} from '@chakra-ui/core';
import { useState, useRef, useEffect } from 'react';
import Head from 'next/head';

const Msg = (props) => {
  const myRef = useRef(null);
  useEffect(() => {
    //myRef.current.scrollIntoView();
  }, []);
  return (
    <Flex
      ref={myRef}
      flexDirection='column'
      justifyContent='center'
      alignItems='flex-end'
      w='95%'
    >
      <Flex>
        <span style={{ color: '#a8aab1', marginRight: '20px' }}>
          {moment().format('DD MM YYYY hh mm A')}
        </span>
        <span>Joseph</span>
      </Flex>
      <div classNameName='message wow-animate'>{props.children}</div>
    </Flex>
  );
};
export const Live = (props) => {
  const [msgs, setMsgs] = useState([]);
  const [notes, setNotes] = useState([]);
  const [textNote, setTextNote] = useState('');
  const [text, setText] = useState('');

  const sendMsg = () => {
    let newmsg = [...msgs];

    if (text != '') {
      newmsg.push(text);
      setMsgs(newmsg);
      setText('');
    }
  };
  const SendNote = () => {
    let newNotes = [...notes];
    if (textNote != '') {
      newNotes.push(textNote);
      setNotes(newNotes);
      setTextNote('');
    }
  };
  useEffect(() => {
    OpenPay.setSandboxMode(true);
    OpenPay.setId('mqrfjs0h2c8pxopp6c63');
    OpenPay.setApiKey('pk_0da9497fbffc4c5996b42ce351972d92');
  }, []);
  const createToken = (e) => {
    e.preventDefault();
    OpenPay.token.extractFormAndCreate(
      $('#processCard'),
      successCard,
      errorCard
    );
  };
  const successCard = () => {};

  const errorCard = () => {};
  return (
    <Layout>
      <Head>
        <head>
          <script
            type='text/javascript'
            src='https://ajax.googleapis.com/ajax/libs/jquery/1.11.0/jquery.min.js'
          ></script>
          <script
            type='text/javascript'
            src='https://js.openpay.mx/openpay.v1.min.js'
          ></script>
          <script
            type='text/javascript'
            src='https://js.openpay.mx/openpay-data.v1.min.js'
          ></script>
        </head>
      </Head>
      <SimpleGrid columns={[1, 0, 2]}>
        <Box>
          <form id='processCard' name='processCard'>
            <img
              class='img-responsive pull-right'
              src='http://i76.imgup.net/accepted_c22e0.png'
            />
            <p>Holder Name:</p>
            <Input
              variant='filled'
              data-openpay-card='holder_name'
              size='50'
              type='text'
              placeholder='Holder name'
            />

            <p>Card number:</p>
            <Input
              variant='filled'
              data-openpay-card='card_number'
              size='50'
              type='text'
            />
            <p>Expiration year:</p>
            <Input
              variant='filled'
              data-openpay-card='expiration_year'
              size='4'
              type='text'
            />
            <p>Expiration month:</p>
            <Input
              variant='filled'
              data-openpay-card='expiration_month'
              size='4'
              type='text'
            />
            <p>cvv2:</p>
            <Input
              variant='filled'
              data-openpay-card='cvv2'
              size='5'
              type='text'
            />
            <p>Street:</p>
            <Input
              variant='filled'
              data-openpay-card-address='line1'
              size='20'
              type='text'
            />
            <p>Number:</p>
            <Input
              variant='filled'
              data-openpay-card-address='line2'
              size='20'
              type='text'
            />
            <p>References:</p>
            <Input
              variant='filled'
              data-openpay-card-address='line3'
              size='20'
              type='text'
            />
            <p>Postal code:</p>
            <Input
              variant='filled'
              data-openpay-card-address='postal_code'
              size='6'
              type='text'
            />
            <p>City:</p>
            <Input
              variant='filled'
              data-openpay-card-address='city'
              size='20'
              type='text'
            />
            <p>State:</p>
            <Input
              variant='filled'
              data-openpay-card-address='state'
              size='20'
              type='text'
            />
            <p>Country code:</p>
            <Input
              variant='filled'
              data-openpay-card-address='country_code'
              size='3'
              type='text'
            />
            <Button
              variantColor='teal'
              onClick={createToken}
              id='makeRequestCard'
              type='button'
              value='Make Card'
            >
              PAGAR
            </Button>
          </form>
        </Box>
      </SimpleGrid>
    </Layout>
  );
};
export default Live;
