import ResponsiveMenu from 'react-responsive-navbar';
import { Button, Box, Flex } from '@chakra-ui/core';
import { RiMenuLine, RiCloseLine } from 'react-icons/ri';
const Navbar = (props) => {
  return (
    <ResponsiveMenu
      menuOpenButton={
        <Flex p='20px' justifyContent='space-between' alignItems='center'>
          LOGO
          <Box as={RiMenuLine} />
        </Flex>
      }
      menuCloseButton={
        <Flex p='20px' justifyContent='space-between' alignItems='center'>
          LOGO
          <Box as={RiCloseLine} />
        </Flex>
      }
      changeMenuOn='500px'
      largeMenuClassName='large-menu-classname'
      smallMenuClassName='small-menu-classname'
      menu={
        <div>
          <Box className='item logo'>
            <Box className='grow-sm'>Logo</Box>
            <Box className='opts'>
              <Box className='grow-sm'>Cursos Presenciales</Box>
              <Box className='grow-sm'>Nostros</Box>
              <Box className='grow-sm'>Crewsoft</Box>
              <Box className='grow-sm'>Crewdemy live</Box>
            </Box>
          </Box>

          <div className='button'>
            <Button className='button' variantColor='teal'>
              Ingresar
            </Button>
          </div>
        </div>
      }
    />
  );
};
export default Navbar;
